package com.water.imemo.activities;import com.water.imemo.R;import com.water.imemo.utils.Constants;import android.app.Activity;import android.os.Bundle;import android.util.Log;import android.widget.EditText;public class EditorActivity extends Activity{		private final String TAG = EditorActivity.class.getSimpleName();		private EditText editText;		@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.editor);		editText = (EditText) findViewById(R.id.memoEditor);				String openType = savedInstanceState.getString("openType");		Log.d(TAG, "openType:"+openType);		if(openType.equals(Constants.NO_MEMO_START_EDITOR)){			Log.d(TAG, "no memo!");			editText.setText("no memo! please input!");		}else{					}	}			}