package com.water.imemo.activities;import com.water.imemo.R;import com.water.imemo.bean.MyMemo;import com.water.imemo.db.DBUtils;import com.water.imemo.db.TableMyMemo;import com.water.imemo.utils.Constants;import android.app.Activity;import android.database.sqlite.SQLiteDatabase;import android.os.Bundle;import android.util.Log;import android.view.View;import android.view.View.OnClickListener;import android.widget.Button;import android.widget.EditText;public class EditorActivity extends Activity{		private final String TAG = EditorActivity.class.getSimpleName();		private EditText editText;	private Button saveButton;		private int memoID = -1;		@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.editor);		editText = (EditText) findViewById(R.id.memoEditor);		saveButton = (Button) findViewById(R.id.editorSaveButton);				saveButton.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View view) {				String content = editText.getText().toString();				saveMemo(memoID, content);			}		});				String openType = getIntent().getStringExtra("openType");		memoID = getIntent().getIntExtra("memoID", -1);				Log.d(TAG, "openType:"+openType);		if(openType.equals(Constants.NO_MEMO_START_EDITOR)){			Log.d(TAG, "no memo!");			editText.setText("no memo! please input!");		}else{					}	}		private void saveMemo(int id , String content){		SQLiteDatabase database = DBUtils.getInstance().getDataBase();		TableMyMemo tableMemo = DBUtils.getInstance().getMyMemoDB();		if(id != -1){			MyMemo memo = tableMemo.getMemo(database, id);			memo.setContent(content);			memo.setModifytime(System.currentTimeMillis());			tableMemo.updateMemo(database, memo);		}else{			MyMemo memo = new MyMemo();			memo.setContent(content);			memo.setModifytime(System.currentTimeMillis());			memo.setPosttime(System.currentTimeMillis());			tableMemo.addMemo(database, memo);		}	}			}