package com.water.imemo.bean;import java.io.Serializable;public class MyMemo implements Serializable {	private static final long serialVersionUID = -1161085547925713849L;		private int id;	private String content;	private long posttime;	private long modifytime;		public int getId() {		return id;	}	public void setId(int id) {		this.id = id;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}		public long getPosttime() {		return posttime;	}	public void setPosttime(long posttime) {		this.posttime = posttime;	}	public long getModifytime() {		return modifytime;	}	public void setModifytime(long modifytime) {		this.modifytime = modifytime;	}		}